import React from "react";
import {
  Button,
  Card,
  CardBody,
  Container,
  Modal,
  ModalHeader,
  ModalBody,
  Row,
  Col,
} from "reactstrap";
import DemoNavbar from "components/Navbars/DemoNavbar.js";
import SimpleFooter from "components/Footers/SimpleFooter.js";

class Theory extends React.Component {
  state = {
    defaultModal: false,
    clickedImage: null,
  };

  toggleModal = () => {
    this.setState({
      defaultModal: !this.state.defaultModal,
    });
  };

  handleImageClick = (image) => {
    this.setState({
      clickedImage: image,
      defaultModal: true,
    });
  };

  render() {
    const images = [
      {
        src:
          "https://media.istockphoto.com/id/637087300/th/%E0%B9%80%E0%B8%A7%E0%B8%84%E0%B9%80%E0%B8%95%E0%B8%AD%E0%B8%A3%E0%B9%8C/%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B9%8C%E0%B8%95%E0%B8%B9%E0%B8%99%E0%B8%81%E0%B8%A3%E0%B8%B0%E0%B8%97%E0%B8%B4%E0%B8%87%E0%B8%99%E0%B9%88%E0%B8%B2%E0%B8%A3%E0%B8%B1%E0%B8%81.jpg?s=612x612&w=0&k=20&c=9a1A3k-eo9rEyD0hJdV7RWmjkbC01Rfy_xVO90UcHVw=" ,
        title: "กระทิง",
        description: "คำอธิบายยยย ความหมายของกระทิง",
      },
      {
        src:
          "https://media.istockphoto.com/id/1267627612/th/%E0%B9%80%E0%B8%A7%E0%B8%84%E0%B9%80%E0%B8%95%E0%B8%AD%E0%B8%A3%E0%B9%8C/%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B9%8C%E0%B8%95%E0%B8%B9%E0%B8%99%E0%B8%99%E0%B8%81%E0%B8%AD%E0%B8%B4%E0%B8%99%E0%B8%97%E0%B8%A3%E0%B8%B5%E0%B8%9A%E0%B8%99%E0%B8%9E%E0%B8%B7%E0%B9%89%E0%B8%99%E0%B8%AB%E0%B8%A5%E0%B8%B1%E0%B8%87%E0%B8%AA%E0%B8%B5%E0%B8%82%E0%B8%B2%E0%B8%A7.jpg?s=612x612&w=0&k=20&c=04igQlAxnItrPSit5cGahmaS2kgXMobm5745rYc8QLo=",
        title: "อินทรี",
        description: "คำอธิบายยยย ความหมายของอินทรี",
      },
      {
        src:
          "https://i.pinimg.com/736x/21/dc/12/21dc12f7bc2dbbacdb54ceabf4146f3f.jpg",
        title: "หนู",
        description: "คำอธิบายยยย ความหมายของหนู",
      },
      {
        src:
          "https://media.istockphoto.com/id/1410564707/th/%E0%B9%80%E0%B8%A7%E0%B8%84%E0%B9%80%E0%B8%95%E0%B8%AD%E0%B8%A3%E0%B9%8C/%E0%B8%AB%E0%B8%A1%E0%B8%B5%E0%B8%99%E0%B8%B1%E0%B9%88%E0%B8%87%E0%B9%80%E0%B8%A8%E0%B8%A3%E0%B9%89%E0%B8%B2%E0%B8%99%E0%B9%88%E0%B8%B2%E0%B8%A3%E0%B8%B1%E0%B8%81%E0%B8%AA%E0%B8%A3%E0%B9%89%E0%B8%B2%E0%B8%87%E0%B8%AA%E0%B8%A3%E0%B8%A3%E0%B8%84%E0%B9%8C-kawaii-%E0%B8%81%E0%B8%B2%E0%B8%A3%E0%B9%8C%E0%B8%95%E0%B8%B9%E0%B8%99%E0%B9%82%E0%B8%A5%E0%B9%82%E0%B8%81%E0%B9%89%E0%B8%A1%E0%B8%B2%E0%B8%AA%E0%B8%84%E0%B8%AD%E0%B8%95.jpg?s=612x612&w=0&k=20&c=6CWIlveQW_-nrILF3qQx1Iq1GUT_36CF23qGpFHTWB8=",
        title: "หมี",
        description: "Description of Image 4",
      },
    ];

    return (
      <>
        <DemoNavbar />
        <main ref="main">
          <section className="section section-shaped section-lg">
            <div className="shape shape-style-1 bg-gradient-default">
              <span />
              <span />
              <span />
              <span />
              <span />
              <span />
              <span />
              <span />
            </div>
            <Container>
              <Row>
                {images.map((image, index) => (
                  <Col md="6" key={index}>
                    <Card className="mb-4">
                      <CardBody>
                        <img
                          src={image.src}
                          className="img-fluid mb-4"
                          style={{ width: "100%" }}
                          alt={image.title}
                          onClick={() => this.handleImageClick(image)}
                        />
                      </CardBody>
                    </Card>
                  </Col>
                ))}
              </Row>
            </Container>
            <Modal
              className="modal-dialog-centered"
              isOpen={this.state.defaultModal}
              toggle={this.toggleModal}
            >
              <ModalHeader toggle={this.toggleModal}>
                {this.state.clickedImage && this.state.clickedImage.title}
              </ModalHeader>
              <ModalBody>
                {this.state.clickedImage && (
                  <p>{this.state.clickedImage.description}</p>
                )}
              </ModalBody>
            </Modal>
          </section>
        </main>
        <SimpleFooter />
      </>
    );
  }
}

export default Theory;

